state_machine.definition:
{
   "Comment": "A Hello World example of the Amazon States Language using Pass states",
   "StartAt": "Hello",
   "States": {
      "Hello": {
         "Next": "New",
         "Type": "Pass"
      },
      "New": {
         "Next": "World",
         "Seconds": 120,
         "Type": "Wait"
      },
      "World": {
         "Comment": "great!!!",
         "End": true,
         "Parameters": {
            "Bucket": "test-hoge"
         },
         "Resource": "arn:aws:states:::aws-sdk:s3:listObjects",
         "Type": "Task"
      }
   }
}
